import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";

const images = [
  { src: "/images/hero1.jpg", text: "DÃ©couvrez nos services", link: "#services" },
  { src: "/images/hero2.jpg", text: "Expertise en Data Science", link: "#expertises" },
  { src: "/images/hero3.jpg", text: "Solutions IA et Big Data", link: "#contact" }
];

const HomePage = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen bg-gray-100">

      {/* Barre de navigation */}
      <header className="bg-white shadow-md py-4 fixed top-0 w-full z-50">
        <div className="container mx-auto flex items-center justify-between px-6 relative">

          {/* Slimdata alignÃ© complÃ¨tement Ã  gauche */}
          <div className="absolute left-6 flex items-center">
            <h1 className="text-3xl font-extrabold text-gray-800 tracking-wide font-serif">
              Slim<span className="text-blue-600">Data</span>
            </h1>
          </div>

          {/* Navigation centrÃ©e en haut */}
          <nav className="absolute left-1/2 transform -translate-x-1/2 flex space-x-6">
            <motion.a href="#expertises" 
              className="bg-gray-200 hover:bg-blue-600 text-gray-700 hover:text-white py-2 px-5 rounded-full font-semibold transition shadow-md"
              whileHover={{ scale: 1.05 }} 
              transition={{ duration: 0.3 }}>
              Expertises
            </motion.a>
            <motion.a href="#secteurs" 
              className="bg-gray-200 hover:bg-blue-600 text-gray-700 hover:text-white py-2 px-5 rounded-full font-semibold transition shadow-md"
              whileHover={{ scale: 1.05 }} 
              transition={{ duration: 0.3 }}>
              Secteurs
            </motion.a>
            <motion.a href="/contact" 
              className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-5 rounded-full font-semibold transition shadow-md"
              whileHover={{ scale: 1.05 }} 
              transition={{ duration: 0.3 }}>
              Contact
            </motion.a>
          </nav>
        </div>
      </header>

      {/* Reste du code intact */}


      {/* Menu DÃ©roulant LatÃ©ral */}
      <AnimatePresence>
        {menuOpen && (
          <motion.div 
            initial={{ x: -250, opacity: 0 }} 
            animate={{ x: 0, opacity: 1 }} 
            exit={{ x: -250, opacity: 0 }} 
            transition={{ duration: 0.3 }}
            className="fixed top-0 left-0 w-64 h-full bg-white shadow-lg p-6 z-50"
          >
            {/* Bouton pour fermer le menu */}
            <button 
              className="text-gray-700 text-3xl mb-6 focus:outline-none"
              onClick={() => setMenuOpen(false)}
            >
              â
            </button>
            <nav className="flex flex-col space-y-4">
              <a href="#about" className="text-gray-700 hover:text-blue-600">Ã Propos</a>
              <a href="#services" className="text-gray-700 hover:text-blue-600">Services</a>
              <a href="#career" className="text-gray-700 hover:text-blue-600">CarriÃ¨res</a>
              <a href="#blog" className="text-gray-700 hover:text-blue-600">Blog</a>
            </nav>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Slider (Carrousel) CentrÃ© */}
      <section className="flex justify-center items-center h-[70vh] mt-[-30px]">
        <div className="relative w-11/12 max-w-6xl h-[500px] overflow-hidden border-4 border-gray-300 rounded-lg shadow-lg">
          <AnimatePresence>
            <motion.div 
              key={currentIndex}
              initial={{ opacity: 0, scale: 1.05 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 1.05 }}
              transition={{ duration: 1 }}
              className="absolute inset-0 flex items-center justify-center bg-cover bg-center"
              style={{ backgroundImage: `url(${images[currentIndex].src})` }}
            >
              <div className="absolute inset-0 bg-black opacity-50 rounded-lg"></div>
              <div className="relative z-10 text-white text-center">
                <h1 className="text-3xl font-bold mb-4">{images[currentIndex].text}</h1>
                <a href={images[currentIndex].link} className="mt-4 bg-blue-600 hover:bg-blue-700 text-white py-3 px-6 rounded-full font-semibold shadow-md">
                  En savoir plus
                </a>
              </div>
            </motion.div>
          </AnimatePresence>
        </div>
      </section>

      {/* Nos Expertises */}
      <section id="expertises" className="py-10 bg-gray-100 text-center">
        <h2 className="text-3xl font-bold mb-8">Nos Expertises</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-5xl mx-auto">
<Link to="/data-science" className="block">
<div className="bg-white p-6 shadow-md rounded-lg hover:bg-blue-100 transition">
<h3 className="text-xl font-semibold">Data Science & IA</h3>
<p className="text-gray-600">DÃ©veloppement d'algorithmes avancÃ©s.</p>
</div></Link>
<Link to="/analyse-predictive" className="block">
<div className="bg-white p-6 shadow-md rounded-lg hover:bg-blue-100 transition">
<h3 className="text-xl font-semibold">Analyse PrÃ©dictive</h3>
<p className="text-gray-600">Anticipez les tendances grÃ¢ce aux modÃ¨les prÃ©dictifs.</p>
</div></Link>
<Link to="/cloud-big-data" className="block">
<div className="bg-white p-6 shadow-md rounded-lg hover:bg-blue-100 transition">
<h3 className="text-xl font-semibold">Cloud & Big Data</h3>
<p className="text-gray-600">Infrastructure et solutions cloud adaptÃ©es.</p>
</div></Link>

        </div>
