import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";

const images = [
  { src: "/images/hero1.jpg", text: "Découvrez nos services", link: "#services" },
  { src: "/images/hero2.jpg", text: "Expertise en Data Science", link: "#expertises" },
  { src: "/images/hero3.jpg", text: "Solutions IA et Big Data", link: "#contact" }
];

const HomePage = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  return (
    <div className="min-h-screen bg-gray-100">

      {/* Header avec menu burger et navigation */}
      <header className="bg-white shadow-md py-4 fixed top-0 w-full z-50">
        <div className="container mx-auto flex items-center justify-between px-6">

          {/* Menu Burger - Aligné tout à gauche */}
          <button 
            className="text-3xl text-gray-700 focus:outline-none"
            onClick={() => setMenuOpen(true)}>
            ☰
          </button>

          {/* Slimdata en texte stylisé */}
          <div className="absolute left-1/2 transform -translate-x-1/2">
              <h1 className="text-3xl font-extrabold text-gray-800 tracking-wide font-serif">
                  Slim<span className="text-blue-600">data</span>
              </h1>
          </div>

          {/* Navigation Alignée à Droite */}
          <nav className="flex space-x-4">
            <motion.a href="#expertises" className="bg-gray-200 hover:bg-blue-600 text-gray-700 hover:text-white py-2 px-5 rounded-full font-semibold transition shadow-md">
              Expertises
            </motion.a>
            <motion.a href="#secteurs" className="bg-gray-200 hover:bg-blue-600 text-gray-700 hover:text-white py-2 px-5 rounded-full font-semibold transition shadow-md">
              Secteurs
            </motion.a>
            <motion.a href="#contact" className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-5 rounded-full font-semibold transition shadow-md">
              Contact
            </motion.a>
          </nav>
        </div>
      </header>

      {/* Slider (Carrousel) Centré */}
      <section className="flex justify-center items-center h-[70vh] mt-10">
        <div className="relative w-11/12 max-w-6xl h-[500px] overflow-hidden border-4 border-gray-300 rounded-lg shadow-lg">
          <AnimatePresence>
            <motion.div key={currentIndex} initial={{ opacity: 0, scale: 1.05 }} animate={{ opacity: 1, scale: 1 }} exit={{ opacity: 0, scale: 1.05 }} transition={{ duration: 1 }} className="absolute inset-0 flex items-center justify-center bg-cover bg-center" style={{ backgroundImage: `url(${images[currentIndex].src})` }}>
              <div className="absolute inset-0 bg-black opacity-50 rounded-lg"></div>
              <div className="relative z-10 text-white text-center">
                <h1 className="text-3xl font-bold mb-4">{images[currentIndex].text}</h1>
                <a href={images[currentIndex].link} className="mt-4 bg-blue-600 hover:bg-blue-700 text-white py-3 px-6 rounded-full font-semibold shadow-md">
                  En savoir plus
                </a>
              </div>
            </motion.div>
          </AnimatePresence>

          {/* Flèches de navigation */}
          <button className="absolute top-1/2 left-4 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-3 rounded-full" onClick={prevSlide}>
            ❮
          </button>
          <button className="absolute top-1/2 right-4 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-3 rounded-full" onClick={nextSlide}>
            ❯
          </button>
        </div>
      </section>

      {/* Nos Expertises */}
      <section id="expertises" className="py-10 bg-gray-100 text-center">
        <h2 className="text-3xl font-bold mb-8">Nos Expertises</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-5xl mx-auto">
          <motion.div className="bg-white p-6 shadow-md rounded-lg">Data Science & IA</motion.div>
          <motion.div className="bg-white p-6 shadow-md rounded-lg">Analyse Prédictive</motion.div>
          <motion.div className="bg-white p-6 shadow-md rounded-lg">Cloud & Big Data</motion.div>
        </div>
      </section>

      {/* Contact */}
      <section id="contact" className="py-16 bg-white text-center">
        <h2 className="text-3xl font-bold mb-6">Contactez-nous</h2>
        <p className="text-lg text-gray-600 max-w-2xl mx-auto mb-6">Prenez rendez-vous avec nos experts.</p>
        <motion.a href="mailto:contact@slimdata.com" className="bg-blue-600 hover:bg-blue-700 text-white py-3 px-6 rounded-full font-semibold shadow-md">
          Envoyer un email
        </motion.a>
      </section>

      {/* Footer */}
      <footer className="bg-gray-900 text-white text-center py-6">
        <p>&copy; 2024 Slimdata. Tous droits réservés.</p>
      </footer>

    </div>
  );
};

export default HomePage;
