import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Link } from "react-router-dom";

const images = [
  { src: "/images/hero1.jpg", text: "Découvrez nos services", link: "#services" },
  { src: "/images/hero2.jpg", text: "Expertise en Data Science", link: "#expertises" },
  { src: "/images/hero3.jpg", text: "Solutions IA et Big Data", link: "#contact" }
];

const HomePage = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen bg-gray-100">

      {/* Barre de navigation */}
      <header className="bg-white shadow-md py-4 fixed top-0 w-full z-50">
        <div className="container mx-auto flex items-center justify-between px-6 relative">

          {/* Slimdata aligné complètement à gauche */}
          <div className="flex items-center">
            <h1 className="text-3xl font-extrabold text-gray-800 tracking-wide font-serif">
              Slim<span className="text-blue-600">Data</span>
            </h1>
          </div>

          {/* Navigation centrée en haut */}
          <nav className="absolute left-1/2 transform -translate-x-1/2 flex space-x-6">
            <motion.a href="#expertises" 
              className="bg-gray-200 hover:bg-blue-600 text-gray-700 hover:text-white py-2 px-5 rounded-full font-semibold transition shadow-md"
              whileHover={{ scale: 1.05 }} 
              transition={{ duration: 0.3 }}>
              Expertises
            </motion.a>
            <motion.a href="#secteurs" 
              className="bg-gray-200 hover:bg-blue-600 text-gray-700 hover:text-white py-2 px-5 rounded-full font-semibold transition shadow-md"
              whileHover={{ scale: 1.05 }} 
              transition={{ duration: 0.3 }}>
              Secteurs
            </motion.a>
            <motion.a href="/contact" 
              className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-5 rounded-full font-semibold transition shadow-md"
              whileHover={{ scale: 1.05 }} 
              transition={{ duration: 0.3 }}>
              Contact
            </motion.a>
          </nav>
        </div>
      </header>

      {/* Slider (Carrousel) Centré */}
      <section className="flex justify-center items-center h-[70vh] mt-20">
        <div className="relative w-11/12 max-w-6xl h-[500px] overflow-hidden border-4 border-gray-300 rounded-lg shadow-lg">
          <AnimatePresence>
            <motion.div 
              key={currentIndex}
              initial={{ opacity: 0, scale: 1.05 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 1.05 }}
              transition={{ duration: 1 }}
              className="absolute inset-0 flex items-center justify-center bg-cover bg-center"
              style={{ backgroundImage: `url(${images[currentIndex].src})` }}
            >
              <div className="absolute inset-0 bg-black opacity-50 rounded-lg"></div>
              <div className="relative z-10 text-white text-center">
                <h1 className="text-3xl font-bold mb-4">{images[currentIndex].text}</h1>
                <a href={images[currentIndex].link} className="mt-4 bg-blue-600 hover:bg-blue-700 text-white py-2 px-6 rounded-full font-semibold shadow-md">
                  En savoir plus
                </a>
              </div>
            </motion.div>
          </AnimatePresence>
        </div>
      </section>
      {/* Nos Expertises */}
      <section id="expertises" className="py-12 bg-gray-100 text-center">
        <h2 className="text-3xl font-bold mb-8">Nos Expertises</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-5xl mx-auto">
          <Link to="/expertise/data-science">
            <motion.div whileHover={{ scale: 1.05 }} className="bg-white p-6 shadow-md rounded-lg cursor-pointer">
              <h3 className="text-xl font-semibold">Data Science & IA</h3>
              <p className="text-gray-600">Développement d'algorithmes avancés pour extraire de la valeur de vos données.</p>
            </motion.div>
          </Link>
          <Link to="/expertise/analyse-predictive">
            <motion.div whileHover={{ scale: 1.05 }} className="bg-white p-6 shadow-md rounded-lg cursor-pointer">
              <h3 className="text-xl font-semibold">Analyse Prédictive</h3>
              <p className="text-gray-600">Anticipez les tendances grâce à des modèles prédictifs performants.</p>
            </motion.div>
          </Link>
          <Link to="/expertise/cloud-bigdata">
            <motion.div whileHover={{ scale: 1.05 }} className="bg-white p-6 shadow-md rounded-lg cursor-pointer">
              <h3 className="text-xl font-semibold">Cloud & Big Data</h3>
              <p className="text-gray-600">Infrastructure et solutions cloud adaptées à vos besoins.</p>
            </motion.div>
          </Link>
        </div>
      </section>

      {/* Secteurs d'Activités */}
      <section id="secteurs" className="py-12 bg-gray-100 text-center">
        <h2 className="text-3xl font-bold mb-8">Secteurs d'Activités</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-5xl mx-auto">
          <motion.div whileHover={{ scale: 1.05 }} className="bg-white p-6 shadow-md rounded-lg">
            <h3 className="text-xl font-semibold">Finance</h3>
            <p className="text-gray-600">Solutions pour la gestion des risques et l'analyse financière.</p>
          </motion.div>
          <motion.div whileHover={{ scale: 1.05 }} className="bg-white p-6 shadow-md rounded-lg">
            <h3 className="text-xl font-semibold">Santé</h3>
            <p className="text-gray-600">Optimisation des services médicaux grâce à la data science.</p>
          </motion.div>
          <motion.div whileHover={{ scale: 1.05 }} className="bg-white p-6 shadow-md rounded-lg">
            <h3 className="text-xl font-semibold">Retail</h3>
            <p className="text-gray-600">Analyse des tendances et gestion de l'inventaire.</p>
          </motion.div>
        </div>
      </section>

      {/* Footer */}
      <footer className="bg-gray-900 text-white text-center p-6">
        <p>&copy; 2024 Slimdata. Tous droits réservés.</p>
      </footer>
    </div>
  );
};

export default HomePage;
